function llenarEventoSalon(a){for(var t={LUNES:0,MARTES:1,MIERCOLES:2,JUEVES:3,VIERNES:4},n={7:0,8:1,9:2,10:3,11:4,12:5,13:6,14:7,15:8,16:9,17:10,18:11,19:12},o=0;o<a.length;o++){var r=a[o],d=r.materia,e=r.profesor,i=r.dia,l=r.salon.split("-"),E=l[0],s=l[1],S=r.hora,v=r.duracion,f=1+o,p=d+"</br>"+e,c='<li class="tt-event btn-info" data-id="'+f+'" data-day="'+t[i]+'" data-start="'+n[S]+'" data-duration="'+v+'">'+p+"</li>",u="#salon"+s+" .tt-events";$(u).append(c)}}